import React, { useState, useEffect } from 'react';
import getMenu from '../data/iceCreamData'; 
import IceCreamImage  from './IceCreamImage';
import LoaderMessage from '../structure/LoaderMessage';
import { useNavigate }  from 'react-router-dom';
import FocusLink from '../structure/FocusLink'; 
import Main from '../structure/Main';


const Menu = () => {
    let history = useNavigate(); 
    const [menu, setMenu] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        let isMounted = true;
        getMenu().then( menuData => {
            if(isMounted){
                setMenu(menuData);
                setIsLoading(false);
                console.log(menuData)

            }

        });

        return () => {
            isMounted = false;
        }
    }, []);

    const onItemClickHandler = to => {

        console.log('history', history)
        history(to , { focus : true });
    };

    const onLinkClickHandler = e => {
        e.stopPropagation();
    };


    return <Main headingText="Rock your state buds with one of these!" > 

        <LoaderMessage 
            loadingMessage="Loading Menu" 
            isLoading = {isLoading} 
            doneMessage = "Loading Menu complete" > 

        </LoaderMessage>
        { menu.length > 0 ? (
        <ul className="container">
            { menu.map(( { id, iceCream, price, description,  inStock, quantity } ) => (
            <li key={id.toString()}> 
                <section className="card"
                         onClick={()=>{
                             onItemClickHandler(`/menu-items/${id.toString()}`)
                         }}
                       >
                    <div className="image-container">
                        <IceCreamImage iceCreamId={iceCream.id} />
                    </div>
                    <div className="text-container">
                        <h3><FocusLink to={`/menu-items/${id}`}
                                  onClick={()=>{
                                    onLinkClickHandler(`/menu-items/${id.toString()}`)
                                  }}
                            > { iceCream.name } </FocusLink></h3>
                        <div className="content card-content">
                            <p className="price">
                                { `$${price.toFixed(2)}` }
                            </p>
                            <p className={`stock${inStock?'':' out'}`}>
                                {
                                    inStock?
                                        `${quantity} in stock`:
                                        `Currently out of stock`
                                }
                            </p>
                            <p className="description"> {process.env.PUBLIC_URL}
                                {description} 
                            </p>
                        </div>
                    </div>
                </section>
            </li> ))}
        </ul>
        ) : (
           !isLoading && <p>Your menu is empty! The sadness!!</p>
        )}

    </Main>
}



// Menu.propTypes = {
//     history: PropTypes.shape({
//       push: PropTypes.func.isRequired,
//     }),
//   };

export default Menu;
