const LoaderMessage = ( {loadingMessage, isLoading } )=> {

    const [ showLoadingMessage , setLoadingMessage ] = useState(false)
    return isLoading ? <p className="loading">  {loadingMessage}</p> : null;
}

export default LoaderMessage;
