import React, { useState, useEffect } from 'react';
import getMenu from '../data/iceCreamData';
import Helmet from 'react-helmet';
import IceCreamImage  from './IceCreamImage';
import LoaderMessage from '../structure/LoaderMessage';
import {Link} from 'react-router-dom';
import PropTypes  from 'prop-types';


const Menu = ({history}) => {

    const [menu, setMenu] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        let isMounted = true;
        getMenu().then( menuData => {
            if(isMounted){
                setMenu(menuData);
                setIsLoading(false);
                console.log(menuData)

            }

        });

        return () => {
            isMounted = false;
        }
    }, []);

    const onItemClickHandler = to => {
        history.push(to);
    };

    return <main>
        <Helmet>
            <title>Rock your taste buds with one of these! | Ultimate Ice Cream</title>
        </Helmet>
        <h2 className="main-heading"> Rock your state buds with one of these!</h2>
        <LoaderMessage 
            loadingMessage="Loading Menu" 
            isLoading = {isLoading}>  
            doneMessage = "Loading Menu complete"  

        </LoaderMessage>
        { menu.length > 0 ? (
        <ul className="container">
            { menu.map(( { id, iceCream, price, description,  inStock, quantity } ) => (
            <li key={id.toString()}> 
                <section className="card"
                         onClick={()=>{
                             alert('llega')
                             onItemClickHandler(`/menu-items/${id}`)
                         }}
                       >
                    <div className="image-container">
                        <IceCreamImage iceCreamId={iceCream.id} />
                    </div>
                    <div className="text-container">
                        <h3><Link to={`/menu-items/${iceCream.id}`}> { iceCream.name } </Link></h3>
                        <div className="content card-content">
                            <p className="price">
                                { `$${price.toFixed(2)}` }
                            </p>
                            <p className={`stock${inStock?'':' out'}`}>
                                {
                                    inStock?
                                        `${quantity} in stock`:
                                        `Currently out of stock`
                                }
                            </p>
                            <p className="description"> {process.env.PUBLIC_URL}
                                {description} 
                            </p>
                        </div>
                    </div>
                </section>
            </li> ))}
        </ul>
        ) : (
           !isLoading && <p>Your menu is empty! The sadness!!</p>
        )}

    </main>
}

Menu.propTypes = {
    history : PropTypes.shape({
        push : PropTypes.func.isRequired,

    })
}

export default Menu;
