import axios from 'axios';

const getMenu = () => {
    return axios.get('api/menu').then( (response) => {
        return response.data.sort((a,b)=>{
            if(a.iceCream.name < b.iceCream.name){
                return -1;
            }
            if(a.iceCream.name > b.iceCream.name){
                return 1;
            }
            return 0;
        })
    });
}

export default getMenu
