import React  , { useEffect, useState , useRef } from 'react';
import { Helmet } from 'react-helmet';
import { getMenuItem } from '../data/iceCreamData';
import { useParams , useNavigate } from 'react-router-dom';
import PropTypes from 'prop-types';
import LoaderMessage from '../structure/LoaderMessage';

const EditIceCream = () => {

    const [isLoading, setIsLoading] = useState(false)
    const [menuItem, setMenuItem] = useState({});
    const { menuItemId } = useParams(); 
    const isMounted = useRef(true);
    let history = useNavigate(); 

    console.log('menuItemId' , menuItemId);


     useEffect(()=>{
        return () => {
            isMounted.current = false;
        }
     }, []);


    useEffect(() => {
        setIsLoading(true);
        getMenuItem(menuItemId)
        .then(({id, price, inStock, quantity, description, iceCream })=>{

            if(isMounted.current){
                console.group('then' , id, price, inStock, quantity, description, iceCream)
                setMenuItem({
                    id,
                    price,// : price.toFix(2),
                    inStock,
                    quantity : quantity.toString(),
                    description : description,
                    iceCream 
                }) 
            }
            setIsLoading(false)
        })
        .catch(err=>{
            if(err.response.status === 404 && isMounted.current){
                history('/', { replace: true })
            }
        });


    }, [menuItemId, history])

    console.log(menuItem)

    return <main>
        <Helmet>
            <title> Update this bauty | Ultimate Ice Cream</title>
        </Helmet>
        <h2 className="main-headig">Update this bauty</h2>
        <LoaderMessage 
            loadingMessage="Loading an Ice Cream"  
            isLoading = {isLoading} 
            doneMessage = "Icrea Cream Loaded" > 


        </LoaderMessage> 
    </main>
}


export default EditIceCream;
