import React  , { useEffect, useState , useRef } from 'react'; 
import { getMenuItem , putMenuItem, deleteMenuItem } from '../data/iceCreamData';
import { useParams , useNavigate } from 'react-router-dom';
import LoaderMessage from '../structure/LoaderMessage';
import useUniqueIds from '../hocks/useUniqueIds';
import Main from '../structure/Main';
import { validateDescription , validateQuantity , validatePrice} from '../util/validators';
import useValidation from '../hocks/useValidation';

import IceCreamImage from './IceCreamImage';
//import '../styles/form-spacer.scss';

import ErrorContainer from './ErrorContainer';

const EditIceCream = () => {

    const [isLoading, setIsLoading] = useState(false);
    const [hasSubmitted, setHasSubmitted] = useState(false);
    const [menuItem, setMenuItem] = useState({ 
        price: '0.00',
        inStock: true,
        description: '',
        iceCream: {id: 0 },
        id: '0'
    });
    const { menuItemId } = useParams(); 
    const isMounted = useRef(true);
    let history = useNavigate(); 

    console.log('menuItemId' , menuItemId);

    const [ descriptionId, 
            descriptionErrorId, 
            stockId, 
            quantityId, 
            quantityErrorId,
            priceId,
            priceErrorId ] = useUniqueIds(7);

    const formRef = useRef(null);        

    const [descriptionError, descriptionErrorProps] = useValidation(
        menuItem.description, 
        descriptionErrorId,
        hasSubmitted,
        validateDescription,
        true);

    const [quantityError, quantityErrorProps ] = useValidation(
        menuItem.quantity, 
        quantityErrorId,
        hasSubmitted,
        validateQuantity, 
        false,
        menuItem.inStock);

    const [priceError, priceErrorProps ] = useValidation(
        menuItem.price, 
        priceErrorId,
        hasSubmitted,
        validatePrice,
        true);

    console.log('descriptionError' , descriptionError)
    console.log('quantityError',quantityError);
    console.log('priceError',priceError);



     useEffect(()=>{
        return () => {
            isMounted.current = false;
        }
     }, []);


    useEffect(() => {
        setIsLoading(true);
        getMenuItem(menuItemId)
        .then(({id, price, inStock, quantity, description, iceCream })=>{

            if(isMounted.current){
                console.group('then' , id, price, inStock, quantity, description, iceCream)
                setMenuItem({
                    id,
                    price,// : price.toFix(2),
                    inStock,
                    quantity : quantity.toString(),
                    description : description,
                    iceCream 
                }) 
            }
            setIsLoading(false)
        })
        .catch(err=>{
            if(err.response.status === 404 && isMounted.current){
                history('/', { replace: true })
            }
        });


    }, [menuItemId, history])

    const onChangeHandler = (e) => {
        let newMenuItemData = {
            ...menuItem,
            [e.target.name]: e.target.type === 'checkbox' ? e.target.checked : e.target.value
        };

        if(e.target.name === 'quantity') {
            console.log('quantity value',e.target.value)
            newMenuItemData.inStock = e.target.value !== '0';
            console.log( 'in stock value', newMenuItemData.inStock)
        }

        if(e.target.name === 'inStock'  && !e.target.checked ){
            newMenuItemData.quantity = '0';

        }

        setMenuItem(newMenuItemData);
    };



    console.log('MENU ITEM' , menuItem)

    const onSubmitHandler = (e) => {
          e.preventDefault();

          setHasSubmitted(true);

        //   console.log('descriptionError' ,descriptionError) 
        //   console.log('priceError' ,priceError) 
        //   console.log('quantityError' ,quantityError)

          if(descriptionError || priceError  || quantityError ) {
            setTimeout(()=> {
                const errorControl = formRef.current.querySelector('[aria-invalid="true"]');
                errorControl.focus(); 
            })

          } else {
            const { id, price, inStock, quantity, description, iceCream } = menuItem; 
            const submitItem = {
                id,
                iceCream: { id : iceCream.id },
                price: parseFloat(price),
                inStock,
                quantity: parseInt(quantity),
                description
            }

            console.log('submitItem' , submitItem)
            putMenuItem(submitItem).then(() => {
              history('/', { replace: true })
            });

          }  

    };

    const onDeleteHandler = () => {

        console.log(' al eliminar menuItemId ', menuItemId)

        deleteMenuItem(menuItemId).then(() => {
            history('/', { focus: true })
        });

    }


    return <Main headingText="Update this bauty">

        <LoaderMessage 
            loadingMessage="Loading an Ice Cream"  
            isLoading = {isLoading} 
            doneMessage = "Icrea Cream Loaded" > 


        </LoaderMessage> 

        {!isLoading && 
          <div className="form-frame">
              <div className="iamge-containe">
                <IceCreamImage iceCreamId={menuItem.iceCream.id}/>
              </div>
              <div className="form-container">
                  <dl>
                      <dt>Name: </dt>
                      <dd>{menuItem.iceCream.name}</dd>
                  </dl>
                  <form onSubmit={onSubmitHandler} noValidate ref={formRef}>
                      <label htmlFor={descriptionId} >Description <span aria-hidden="true">*</span>:</label>
                      <ErrorContainer 
                        errorText = {descriptionError} 
                        hasSubmitted={hasSubmitted}
                        errorId={descriptionErrorId}
                        >
                            <textarea   id={descriptionId}  
                                    name="description" 
                                    rows="3"  
                                    value={menuItem.description}
                                    onChange={onChangeHandler}
                                    {...descriptionErrorProps}
                            />
                      </ErrorContainer>

                      <label htmlFor={stockId} >In Stock:</label>
                      <div className="checkbox-wrapper"> 
                          <input id={stockId} type="checkbox"
                                 name="inStock"
                                 checked={menuItem.inStock}
                                 onChange={onChangeHandler}
                                 ></input>
                          <div className="checkbox-wrapper-checked" />
                      </div>
                      <label htmlFor={quantityId}>Quantity :</label>
                      <ErrorContainer 
                        errorText = {quantityError}  
                        hasSubmitted={hasSubmitted}
                        errorId={quantityErrorId}
                        >
                            <select  id={quantityId} 
                                 name="quantity" 
                                 value={menuItem.quantity} 
                                 onChange={onChangeHandler}
                                 {...quantityErrorProps}
                                 > 
                                <option value="0">0</option>
                                <option value="10">10</option>
                                <option value="20">20</option>
                                <option value="30">30</option>
                                <option value="40">40</option>
                                <option value="50">50</option>
                                <option value="60">60</option>
                                <option value="70">70</option>
                            </select>
                      </ErrorContainer>
                      <label htmlFor={priceId}>Price <span aria-hidden="true">*</span>:</label>
                      <ErrorContainer 
                        errorText = {priceError}  
                        hasSubmitted={hasSubmitted}
                        errorId={priceErrorId}
                        >
                            <input id={priceId} 
                                    type="number" 
                                    name="price" 
                                    step='0.01' 
                                    value={menuItem.price} 
                                    onChange={onChangeHandler}
                                    {...priceErrorProps}>

                            </input>
                      </ErrorContainer>
                      <div className="button-container">
                          <button  className="ok" type="submit">Save</button>
                          <button  className="warning" type="button" onClick={onDeleteHandler}>
                              delete
                          </button>
                      </div>



                  </form>

              </div>
          </div>
        }
    </Main>
}


export default EditIceCream;
