import React  , { useEffect, useState , useRef } from 'react'; 
import { getIceCream  } from '../data/iceCreamData';
import { useParams , useNavigate } from 'react-router-dom';
import LoaderMessage from '../structure/LoaderMessage'; 
import Main from '../structure/Main'; 


import IceCream from './IceCream';

const AddIceCream = ({location}) => {

    const [isLoading, setIsLoading] = useState(false); 
    //const [menuItem, setMenuItem] = useState();

    const [iceCream, setIceCream] = useState( 
                 {  iceCream : {id: 0},
                    price : 0,
                    quantity:0,
                    inStock: false,
                    description: ''});


    const { menuItemId } = useParams(); 
    const isMounted = useRef(true);
    let history = useNavigate(); 



     useEffect(()=>{
        return () => {
            isMounted.current = false;
        }
     }, []);


    useEffect(() => { 
        //setIsLoading(true);
        getIceCream(location.split('=')[1]) //add?itemId=1
        .then((item)=>{
            if(isMounted.current){ 
                setIceCream(item) 
            }
            setIsLoading(false)
        })
        .catch(err=>{
            if(err.response.status === 404 && isMounted.current){
                history('/', { replace: true })
            }
        });


        console.log('2222222')


    }, [ history, location])




    const onSubmitHandler = (menuItem) => { 
        console.log('menuItem', menuItem)
    };




    return (<Main headingText="Add some goodness to the menu">

        <LoaderMessage 
            loadingMessage="Loading an Ice Cream"  
            isLoading = {isLoading} 
            doneMessage = "Icrea Cream Loaded" >  
        </LoaderMessage> 

        {!isLoading && 
          (<IceCream
            iceCream={iceCream} 
            onSubmit={onSubmitHandler}
          />)
        }
    </Main>);
}


export default AddIceCream;
